mapper.insert:

insert into user (id, name, sex, 
  age, userName, password, 
  image, driverCardNumber, driverCardNumberDate, 
  remark, points, level
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{sex,jdbcType=VARCHAR}, 
  #{age,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{image,jdbcType=VARCHAR}, #{driverCardNumber,jdbcType=VARCHAR}, #{driverCardNumberDate,jdbcType=TIMESTAMP}, 
  #{remark,jdbcType=VARCHAR}, #{points,jdbcType=INTEGER}, #{level,jdbcType=VARCHAR}
  )

mapper.select:
SELECT count(*) FROM user u where 1=1
and name like "%"#{userEntity.name,jdbcType=VARCHAR}"%"
and userName = #{userEntity.userName,jdbcType=VARCHAR}
and driverCardNumber = #{userEntity.driverCardNumber,jdbcType=VARCHAR}

select * FROM user u where 1=1
and name like "%"#{userEntity.name,jdbcType=VARCHAR}"%"
and userName = #{userEntity.userName,jdbcType=VARCHAR}
and driverCardNumber = #{userEntity.driverCardNumber,jdbcType=VARCHAR}
ORDER BY u.id DESC
